//
//	demo dual mode module called 'foo'
//

var create_module = function () {
	var theModule = {
		version: "0.0.0",
		author: "Maxwell B Garber",
		date: "2017-10-21"
	};
	
	theModule.foo = function () {
		return "foo";
	};
	
	theModule.bar = function () {
		return "bar";
	};
	
	return theModule;
};
var foo = create_module();

//	CommonJS module export
if (typeof require === 'function') {
	if (typeof define !== 'function') {
		// Node.js environment
		var define = require('amdefine')(module);
		define([], foo);
	} else {
		// browser/RequireJS environment
		define([], foo);
	}
} else {
	var foo = foo();
}

// EOF
